import os
import shutil
import cv2

label_from_PATH = "result/SegmentationClass"
label_to_PATH = "binary_label"

filepath_list = os.listdir(label_from_PATH)
# filepath_list.remove("labelme_json_to_dataset.exe")

# Create the output label directory if it does not exist
if not os.path.isdir(label_to_PATH):
    os.mkdir(label_to_PATH)

# Whether to binarize label images
# If True, grayscale conversion and binary thresholding are applied.
# Label images generated by LabelMe are RGB images. For the first class,
# the color is typically [128, 0, 0]. After RGB-to-grayscale conversion,
# the corresponding pixel value is approximately 38.
# Therefore, a threshold in the range [0, 38] can be used to obtain
# a binary mask where foreground pixels are set to 255 and background to 0.

bin_img = True

for i, file_path in enumerate(filepath_list):
    src_label = "{}".format(os.path.join(label_from_PATH, filepath_list[i]))
    label_name = "{}".format(file_path)

    if bin_img:
        dest_label = cv2.imread(src_label)
        dest_label = cv2.cvtColor(dest_label, cv2.COLOR_BGR2GRAY)
        ret, dest_label = cv2.threshold(dest_label, 0, 255, cv2.THRESH_BINARY)
        cv2.imwrite(
            os.path.join(label_to_PATH, label_name),
            dest_label,
            [cv2.IMWRITE_PNG_COMPRESSION, 9]
        )
    else:
        shutil.copy(src_label, os.path.join(label_to_PATH, label_name))

    print("{} has been copied to {}".format(label_name, label_to_PATH))

print("All done!!!")
